mapper.select:
select re_no, rate, content, writedate, deleted, title, book_no, id from
	(
		select rownum idx, re_no, rate, content, writedate, deleted, title, book_no, id from review
		where book_no=#{book_no} and deleted='0' and  rownum < #{end} 
		order by re_no desc
	)
where idx >= #{beginning}
